var report_key = msg.payload;
if(typeof(report_key) == "string")
{
    var data = flow.get('latest_data')
    var msg1 = {}
    var last_day_data = data.last_day_report[report_key];
    var dateFormat = global.get('utils').methods.formatDatetime;
    var new_last_day_data = {"report_key": report_key};
    if(report_key == "energy")
    {
        new_last_day_data.initial = last_day_data.initial + " KWH";
        new_last_day_data.final = last_day_data.final + " KWH";
        new_last_day_data.total = (last_day_data.final - last_day_data.initial).toFixed(2) + " KWH";
    }
    else if(report_key == "runtime")
    {
        new_last_day_data.initial = last_day_data.initial + " Minutes";
        new_last_day_data.final = last_day_data.final + " Minutes";
        new_last_day_data.total = (last_day_data.final - last_day_data.initial) + " Minutes";
    }
    else if(report_key == "power" || report_key == "voltage" || report_key == "current")
    {
        var unit = '';
        if(report_key == 'power')
            unit = "W";
        else if(report_key == "voltage")
            unit = "V";
        else if(report_key == "current")
            unit = "A";
        new_last_day_data.avg = last_day_data.avg + " " + unit;
        new_last_day_data.maximum = last_day_data.maximum + " " + unit;
        new_last_day_data.minimum = last_day_data.minimum + " " + unit;
        new_last_day_data.maximum_time = dateFormat(last_day_data.maximum_time);
        new_last_day_data.minimum_time = dateFormat(last_day_data.minimum_time);
    }
    msg1.report_key = report_key
    msg1.payload = new_last_day_data;
    return msg1;
}
